mapper.select:
select user_id,tenant_id, nickname, avatar from t_login_user
                                 where
                                     tenant_id = #{tenantId}
and mobile = #{mobile} and password = #{password}
and status = 1

select user_id,tenant_id, nickname, avatar from t_login_user
where
    tenant_id = #{tenantId}
  and email = #{email} and password = #{password}
  and status = 1
